// src/App.js

import React, { useState } from 'react';
import axios from 'axios';
import Dashboard from './components/Dashboard';

function App() {
    const [domain, setDomain] = useState('');
    const [ip, setIp] = useState('');

    const handleSubmit = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/resolve/${domain}`);
            setIp(response.data.ip || 'Not found');
        } catch (error) {
            setIp('Error occurred');
        }
    };

    return (
        <div className="App">
            <h1>DNS Resolver Dashboard</h1>
            <input
                type="text"
                value={domain}
                onChange={(e) => setDomain(e.target.value)}
                placeholder="Enter domain"
            />
            <button onClick={handleSubmit}>Resolve</button>
            <div>
                <h2>Resolved IP: {ip}</h2>
            </div>
            <Dashboard />
        </div>
    );
}

export default App;
